const regexInput=document.getElementById("expresion"),textInput=document.getElementById("text"),resultElement=document.getElementById("result"),checkButton=document.getElementById("check");checkButton.addEventListener("click",()=>{const e=regexInput.value,t=textInput.value,n=new RegExp(e,"g"),c=t.match(n);let u=0;c?(u=c.reduce((e,t)=>e+t.length,0),resultElement.textContent=`Coincidencias encontradas: ${u}`):resultElement.textContent="No hay coincidencias."});